import React, { Component } from 'react';
import TodoDataService from '../api/TodoDataService';
import Authentication from './Authentication';

class ToDoList extends Component {
  state = {
    todos: [],
    message: null
  };

  componentDidMount() {
    let username = Authentication.getLoggedInUsername();
    this.refreshTodos(username);
  }

  refreshTodos = (username) => {
    TodoDataService.retrieveAllTodos(username)
    .then(response => {
      this.setState({
        todos: response.data
      });
    });
  }

  handleDelteClick = (id) => {
    let username = Authentication.getLoggedInUsername();
    TodoDataService.deleteTodo(username, id)
      .then(response => {
        this.setState({
          message: `Todo with id ${id} is successfully deleted!`
        }); 
        this.refreshTodos(username);
      });
  }

  render() {
    return (
      <div className="container">
        <h1>ToDos</h1>
        {this.state.message && <div className="alert alert-successfull">{this.state.message}</div>}
        <table className="table">
        <thead>
          <tr>
            <th>Description</th>
            <th>Due Date</th>
            <th>is it Completed?</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {this.state.todos.map(todo => 
            <tr key={todo.id}>
              <td>{todo.description}</td>
              <td>{todo.targetDate.toString()}</td>
              <td>{todo.done.toString()}</td>
              <td><button className="btn btn-warning" onClick={() => this.handleDelteClick(todo.id)}>Delete</button></td>
            </tr>
          )}
        </tbody>
      </table>
      </div>
    );
  }
}

export default ToDoList;
